mapper.select:
SELECT *
FROM
    (SELECT ROWNUM AS rnum, c.*, u.nickname,
    cl.like_point, cl.dislike_point
FROM comments c
JOIN users u ON c.user_id = u.user_id
JOIN comment_like cl ON c.comment_id = cl.comment_id
WHERE c.post_id = #{post_id}
order by comment_created_time desc
)
WHERE rnum  >=  #{commentStart}
AND rnum  <=  #{commentEnd}
